package main

import "testing"

func TestA(t *testing.T) {
	actual := minimumSemesters(3, [][]int{[]int{1, 3}, []int{2, 3}})

	if actual != 2 {
		t.Fatalf("invalid: %d", actual)
	}
}

func TestB(t *testing.T) {
	actual := minimumSemesters(3, [][]int{[]int{1, 2}, []int{2, 3}, []int{3, 1}})

	if actual != -1 {
		t.Fatalf("invalid: %d", actual)
	}
}

